mapper.update:

	update REVIEW_BOARD 
	set review_title=#{review_title}, review_content=#{review_content}
	<if test="imageFileName != '' and imageFileName != null">
		, imageFileName=#{imageFileName}
	</if>

where review_no=#{review_no}

mapper.select:
SELECT review_level, review_no, review_title, review_content, ID, WRITE_DATE
FROM REVIEW_BOARD
WHERE review_no=#{review_no}

mapper.update:
UPDATE REVIEW_BOARD
SET TITLE=#{review_title}, CONTENT=#{review_content}, 
WHERE review_no=#{review_no}

mapper.select:
SELECT IMAGE_FILE_NO, IMAGE_FILE_NAME, REG_DATE, review_no
FROM REVIEW_IMAGE WHERE review_no=#{review_no}
ORDER BY IMAGE_FILE_NO ASC
