mapper.select:
SELECT LEVEL, ARTICLE_NO, TITLE, CONTENT, IMAGE_FILE_NAME, ID, WRITE_DATE
FROM REVIEW_BOARD
WHERE ARTICLE_NO=#{articleNo}

mapper.update:
UPDATE REVIEW_BOARD
SET TITLE=#{title}, CONTENT=#{content}, IMAGE_FILE_NAME=#{imageFileName}
WHERE ARTICLE_NO=#{articleNo}

mapper.select:
SELECT IMAGE_FILE_NO, IMAGE_FILE_NAME, REG_DATE, ARTICLE_NO
FROM REVIEW_IMAGE WHERE ARTICLE_NO=#{articleNo}
ORDER BY IMAGE_FILE_NO ASC
